package com.info.madhu;

public class ConditionalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		a=10;
		//variable x = (expression) ? value if true : value if false

	int	b=(a==1)?20:30;
		System.out.println(b);
		b=(a==10)?20:30;
		System.out.println(b);

	}

}
